import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

export default function DetailCity() {
    const [city, setCity] = useState('');

    useEffect(() => {
        function detail(){
            const { id } = useParams;
            setCity(id)
        }
        console.log(city)
        detail();
    }, [city]);

    return(
        <div>
            <h1>Lagoa Seca</h1>
            <h2> {city} </h2>
        </div>
    );
}
