import React, { useEffect, useState } from 'react';
import apicidades from '../../services/apicidades';
import './style.css';

export default function Detail() {
    const [city, setCity] = useState([]);
    const [search, setSearch] = useState('');

    useEffect(() => {
        async function loading(){
            const response = await apicidades.get(`?format=json&is_last=True&city=${search}`);
            setCity(response.data.results);
        }
        loading();
    }, [search]);
    return(
        <div>
            <div className='box-city'>
                <label>Cidade</label>
                <input type='text' onChange={(event) => setSearch(event.target.value.toUpperCase())} />
                {city.map(item => (
                    <li>
                        <strong>Cidade: {item.city} </strong>
                        <strong>Confirmados: {item.confirmed} </strong>
                        <strong>Data: {item.date} </strong>
                        <strong>√çmdice de mortalidade: {item.death_rate} </strong>
                        <strong>Mortes: {item.deaths} </strong>
                        <strong>Estado: {item.state} </strong>
                    </li>
                ))}
                <div className='box-button'>
                    <button>back</button>
                    <button>Next</button>
                </div>
            </div>
        </div>
    );
}
