import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';
import './style.css';

export default function Home() {
    const [covid, setCovid] = useState([]);

    useEffect(() => {
        async function loading(){
            const response = await api.get();
            setCovid(response.data.data)
        }
        loading();
    }, []);
    return(
        <div className='container'>
            <div className='box'>
                <h2>Indice da covid-19 por Estado</h2>
                <Link to='/detail'>
                {covid.map(item => (
                    <li key={item.uid}>
                        <strong>UF: {item.uf} </strong>
                        <strong>Estado: {item.state} </strong>
                        <strong>Confirmados: {item.cases} </strong>
                        <strong>Mortes: {item.deaths} </strong>
                        <strong>Suspeitos: {item.suspects} </strong>
                        <strong>Recusados: {item.refuses} </strong>
                        <strong>Data: {item.datetime} </strong>
                    </li>
                ))}
                </Link>
            </div>
        </div>
    );
}
