import React, { useEffect, useState } from 'react';
import api from '../../services/api';
import './style.css';

export default function Home() {
    const [covid, setCovid] = useState([]);

    useEffect(() => {
        async function loading(){
            const response = await api.get();
            setCovid(response.data.data)
        }
        loading();
    }, []);
    return(
        <div className='container'>
            <div className='box'>
                {covid.map(item => (
                    <li key={item.uid}>
                        <strong>Estado: {item.state} </strong>
                        <strong>Casos: {item.cases ? 5000 > 'maior' : 'menor'} </strong>
                        <strong>Mortes: {item.deaths} </strong>
                        <strong>Suspeitos: {item.suspects} </strong>
                        <strong>Recusados: {item.refuses} </strong>
                        <strong>Data: {item.datetime} </strong>
                    </li>
                ))}
            </div>
        </div>
    );
}
